import React from 'react'
import { useParams } from 'react-router-dom'
import { Popup } from 'semantic-ui-react'

const Message = ({ id, content, messaged, username }) => {
    const user = localStorage.getItem('user')
    const { chatUser } = useParams()


    return (
        <div className="py-2 px-3 rounded-pill">
                {(username !== user) ? (
                <Popup
                    content={messaged}
                    inverted
                    position='left center'
                    trigger={
                            <div className="chat-body">
                                <div className="chat-user-message-container chat-message">
                                    <p className="chat-text">{content}</p>
                                </div>
                            </div>
                    }
                />
                
            ) : (
                    <Popup
                        content={messaged}
                        inverted
                        position='right center'
                        trigger={
                                <div className="chat-body">
                                    <div className="bg-primary chat-message">
                                        <p className="chat-text">{content}</p>
                                    </div>
                                </div>
                        }
                    />
            )}
        </div>
    )
}

export default Message
