import React from 'react'
import { useParams } from 'react-router-dom'
import { Button, Icon, Popup } from 'semantic-ui-react'

const Message = ({ usersMessage, content }) => {
    const user = localStorage.getItem('user')
    const { chatUser } = useParams()

    return (
        <>
            {/*If this isn't users message*/}
            {usersMessage !== user ? (
                <div className="d-flex my-3">
                    <div className="py-2 px-3 rounded-pill chat-user-side">
                        <p>{content}</p>
                    </div>
                </div>
            ) : (
                <div className=" d-flex my-3">
                    <Popup
                        content='yor mmama'
                        position="top right"
                        trigger={
                        <div className="ml-auto py-2 px-3 rounded-pill bg-primary">
                            <p className="text-white">{content}</p>
                        </div>}
                    />
                </div>
            )}
       </>
    )
}

export default Message
