import React, { useState, useEffect } from 'react'
import '../App.css';

import Post from './Post'

const Posts = ({ data, openPostDetail }) => {
    const [ posts, setPost ] = useState()

    useEffect(() => {
        setPost(data)
    }, [data])

    return (
        <div className="posts">
            {posts.allPosts.map((movie, index) => {
                return (
                    <Post 
                        key={index} 
                        movie={movie}
                        openPostDetail={openPostDetail}
                    />
                )
            })}
        </div>
    )
}

export default Posts
