import { gql } from "@apollo/client";

//Query
export const USER_LIST_QUERY = gql`
    query {
        allUsers {
            username
            email
        }
    }
`

//Mutation
export const USER_REGISTER_MUTATION = gql`
    mutation register($email: String!, $user: String!, $password1: String!, $password2: String!) {
        register (input: {email: $email, user: $user, password1: $password1, password2: $password2}) {
            user {
                username
            }
        }
    }
`

export const USER_LOGIN_MUTATION = gql`
    mutation loginUser ($username: String!, $password: String!) {
        tokenAuth (username: $username, password: $password) {
            success
            errors
            token
        }   
    }
`

export const USER_REFRESH_TOKEN_MUTATION = gql`
    mutation verifyToken ($token: String!) {
        verifyToken(token: $token) {
            success
            errors
        }
    }
`

export const USER_PROFILE_INFO_MUTATION = gql`
    mutation profileInfo ($user: String!) {
        profileInfo(input: {user: $user}) {
            profile {
                image
                user {
                    username
                    email
                }
            }
        }
    }
`
