import React from 'react'
import { useParams } from 'react-router-dom'

const Message = ({ usersMessage, content }) => {
    const user = localStorage.getItem('user')
    const { chatUser } = useParams()

    return (
        <div>
            {/*If this isn't users message*/}
            {usersMessage !== user ? (
               <div class="rounded-pill bg-primary chat-user-side">
                   <p>{content}</p>
                </div>
            ) : (
                <div className="mt-3 user-side">
                    <p>{content}</p>
                </div>
            )}
       </div>
    )
}

export default Message
