import React from 'react'

const ReplyForm = () => {
    
    const checkForm = () => {
        if (commentRef.current.value) {
            setAllowButton(true)
        }

        else {
            setAllowButton(false)
        }
    }

    return (
        <div>
            <Form onChange={checkForm} reply>
                <textarea ref={commentRef} className="post-create-content-container" placeholder='Enter something...' />
                {(allowButton) ? (
                <Button onClick={handleOnAddReply} className="comment-create-button" content='Add Reply' labelPosition='left' icon='edit' primary />
                ) : (
                    <Button disabled onClick={handleOnAddReply} className="comment-create-button" content='Add Reply' labelPosition='left' icon='edit' primary />
                )}
            </Form>
        </div>
    )
}

export default ReplyForm
