import React from 'react'
import { useMutation, useQuery } from '@apollo/react-hooks'
import { Button } from 'semantic-ui-react'
import { useHistory } from 'react-router-dom'

import { POST_DELETE_MUTATION } from '../../api/post'

const ProfileButtons = ({ id, detailData }) => {
    const user = localStorage.getItem('user')

    const [ postDelete ] = useMutation(POST_DELETE_MUTATION)
    const history = useHistory()
    
    const handleOnDelete = () => {
        postDelete({ variables: { id: id } })
        history.push('/')
    }

    const handleOnEdit = () => {
        history.push(`editPost/${id}`)
    }

    return (
        <>
            {(user === detailData.findPost.post.user.username) ? (
                <>
                    <Button onClick={handleOnEdit} className="post_detail_edit_button" color="teal">Edit</Button>
                    <Button onClick={handleOnDelete} className="post_detail_delete_button" color="red">Delete</Button>
                </>
            ) : null }
        </>
    )
}

export default ProfileButtons
