import React, { useState } from 'react';
import { Route, Switch, Redirect } from 'react-router-dom'
import { useQuery, useMutation } from '@apollo/react-hooks'

//Components
import Navbar from './components/Navbar'
//Posts
import Posts from './components/Posts/Posts'
import PostDetail from './components/Posts/PostDetail'
import PostCreate from './components/Posts/PostCreate'
import PostEdit from './components/Posts/PostEdit'

//Authentication
import Login from './components/Authentication/Login'
import Register from './components/Authentication/Register'

import { POST_LIST_QUERY } from './components/post_query'

function App () {
  const token = localStorage.getItem('token')
  const { data: postData  } = useQuery(POST_LIST_QUERY)


  return (
    <div className="App">
        <Navbar />
        <Switch>
          <Route path="/login" component={() => <Login />} />
          <Route path="/register" component={() => <Register />} />
          {(token) ? (
            <Switch>
              {(postEditData) ? (
                <Route path="/editPost/:id" component={() => <PostEdit postEditData={postEditData} />} />
              ) : null }
              <Route path="/createPost" component={() => <PostCreate />} />
              <Route path="/:id" component={() => <PostDetail />} />
              {(postData) ? (
                <Route path="/" component={() => <Posts  />} />
                ) : null }
            </Switch>  
          ) : (
            <Redirect to='/login' />
          )}
        </Switch>
    </div>
  );
}

export default App;
