import React, { useState, useRef, useEffect } from 'react'
import { Button, Form } from 'semantic-ui-react'

const PostEdit = (postEditData) => {
    const { title, content } = postEditData
    const [ inputData, setInputData ] = useState({title: '', content: ''})

    const titleRef = useRef()
    const contentRef = useRef()

    useEffect(() => {
        setInputData({title: title, content: content})
    }, [postEditData])

    const handleOnSubmit = () => {
        console.log('ez')
    }

    return (
        <div className="post-create-container">
            <Form>
                <Form.Field>
                    <label>Title</label>
                    <input value={inputData.title} ref={titleRef} placeholder='Title' />
                </Form.Field>
                <Form.Field>
                    <label>Content</label>
                    <textarea value={inputData.content} className="post-create-content-container" ref={contentRef} placeholder='Enter something...' />
                </Form.Field>
                <Button onClick={handleOnSubmit} className="submit-button" type='button' primary>Create</Button>
            </Form>
        </div> 
    )
}

export default PostEdit
