import React from 'react'
import { useParams } from 'react-router-dom'
import { Popup } from 'semantic-ui-react'

const Message = ({ id, content, messaged, username }) => {
    const user = localStorage.getItem('user')
    const { chatUser } = useParams()


    return (
        <div>
            {(username !== user) ? (
                <li class="mb-4 in">
                    <div class="chat-body">
                        <div class="chat-user-container chat-message">
                            <p>{content}</p>
                        </div>
                    </div>
                </li>
                ) : (
                    <li class="out">
                    <Popup
                        content='I am positioned to the right center'
                        position='right center'
                        trigger={
                                <div class="chat-body">
                                    <div class="bg-primary chat-message">
                                        <p>{content}</p>
                                    </div>
                                </div>
                        }
                        />
                        </li>
                
            )}
        </div>
    )
}

export default Message
