import React, { useState, useEffect, useRef } from 'react'
import { Button, Form, Image } from 'semantic-ui-react'
import { useMutation } from '@apollo/react-hooks'
import { useHistory, useParams } from 'react-router-dom'
import { USER_PROFILE_INFO_MUTATION } from '../api/user'




const Profile = () => {
    const { user } = useParams()
    const [ profileInfo, { data: profileData } ] = useMutation(USER_PROFILE_INFO_MUTATION)

    const [ allowButton, setAllowButton ] = useState(false)
    const usernameRef = useRef()
    const emailRef = useRef()

    useEffect(() => {
        const getProfileInfo = () => {
            if (user) {
                profileInfo({ variables: { user: user } })
            }
        }
        getProfileInfo()
    }, [user])


    const handleOnSubmit = () => {
        console.log('test')
    }


    const checkForm = () => {
        if (usernameRef.current.value && emailRef.current.value) {
            setAllowButton(true)
        }

        else {
            setAllowButton(false)
        }
    }

    
    return (
        <div className="content-section">
      <div className="media">
        <img className="rounded-circle account-img" src="" />
        <div className="media-body">
          <h2 className="account-heading">username</h2>
          <p className="text-secondary">email</p>
        </div>
      </div>
      <form method="POST" enctype="multipart/form-data">
          <fieldset className="form-group">
              <legend className="border-bottom mb-4">Profile Info</legend>
              s
          </fieldset>
          <div class="form-group">
              <button className="btn btn-outline-info" type="submit">Update</button>
          </div>
      </form>
    </div>
    )
}

export default Profile
