import React, { useState, useRef, useEffect } from 'react'
import { Button, Form, Message } from 'semantic-ui-react'
import { useMutation } from '@apollo/react-hooks'
import { useHistory, Link } from 'react-router-dom'

import { USER_LOGIN_MUTATION, USER_PROFILE_INFO_MUTATION } from '../Api/user'

const Login = () => {
    const history = useHistory()

    const [ failedToLogin, setFailedToLogin ] = useState('') 
    const [ allowButton, setAllowButton ] = useState(false)
    const [ loginUser, { data } ] = useMutation(USER_LOGIN_MUTATION)
    const [ checkUserProfile ] = useMutation(USER_LOGIN_MUTATION)

    const usernameRef = useRef()
    const passwordRef = useRef()

    
    useEffect(() => {
        console.log('test')
        if (data) {
            console.log('test')
            if (data.tokenAuth.success !== true) {
                setFailedToLogin(true)
            }
            else {
                const token = data.tokenAuth.token
                const usernameValue = usernameRef.current.value

                console.log('token')
                /*

                //if user doesnt have profile -> create new one
                checkUserProfile({ variables: { user: usernameValue } })
        
                localStorage.setItem('user', usernameValue)
                localStorage.setItem('token', token)
                window.location.reload(false);
                history.push('/')
                */
            }
        }
    }, [data]) 
    
    const handleOnSubmit = () => {
        const usernameValue = usernameRef.current.value
        const passwordValue = passwordRef.current.value

        loginUser({ variables: {
            username: usernameValue, password: passwordValue
        }})
    }
    
    //Check if username and password was filled
    const checkForm = () => {
        if (usernameRef.current.value && passwordRef.current.value) {
            setAllowButton(true)
        }

        else {
            setAllowButton(false)
        }
    }


    return (
        <div className="login-container">
            {(failedToLogin !== true && failedToLogin !== '') ? (
                <Message
                    className="message-container"
                    error
                    header="Please, enter valid credentials"
                />
            ) : null }
            <Form onChange={checkForm}>
                <Form.Field>
                    <label>Username</label>
                    <input ref={usernameRef} placeholder='Username' />
                </Form.Field>
                <Form.Field>
                    <label>Password</label>
                    <input type="password" ref={passwordRef} placeholder='Password' />
                </Form.Field>
                <Form.Field>
                    <p className="text-muted">Need an an account? <a href="/register" className="ml-2" >Sign up</a></p>
                </Form.Field>
                {/*If both fields were filled -> show undisabled button*/}
                {(allowButton) ? (
                    <Button onClick={handleOnSubmit} className="submit-button" type='button' primary>Login</Button>
                ) : (
                    <Button disabled onClick={handleOnSubmit} className="submit-button" type='button' primary>Login</Button>
                )}
            </Form>
        </div>
    )
}

export default Login
