import React from 'react'

const Message = ({ usersMessage, content }) => {

    return (
          <div>
            {/*If this isn't users message*/}
            {usersMessage !== user ? (
               <div className="chat-user-side">
                   <p>{content}</p>
                </div>
            ) : (
                <div className="mt-3 user-side">
                    <p>{content}</p>
                </div>
            )}
       </div>
    )
}

export default Message
