import React, { useState, useEffect } from 'react'
import { Item, Button, Icon, Label, Comment, Header, Form } from 'semantic-ui-react'
import { useMutation } from '@apollo/react-hooks'
import { useHistory, useParams } from 'react-router-dom'

import { POST_DELETE_MUTATION, POST_FIND_MUTATION, POST_LIKE_MUTATION } from '../api/post'
import { COMMENT_LIST_QUERY } from '../api/comment'

import CustomComment  from '../Comments/CustomComment'

const DEFAULT_IMAGE = 'https://miro.medium.com/max/550/1*TxgjUE2uJuiRUVVmE_kU6g.png'

const PostDetail = () => {
    const { id } = useParams()
    const history = useHistory()
    const user = localStorage.getItem('user')
    const [ likes, setLikes ] = useState(0)

    const [ postDelete ] = useMutation(POST_DELETE_MUTATION)
    const [ postFind, { data: detailData } ] = useMutation(POST_FIND_MUTATION)
    const [ postLike ] = useMutation(POST_LIKE_MUTATION)
    
    useEffect(() => {
        if (detailData) {
            setLikes(detailData.findPost.post.totalLikes)
        }
    }, [detailData])

    useEffect(() => {
        postFind({ variables: { id: id } })
    }, [id])

    const handleOnDelete = () => {
        postDelete({ variables: { id: id } })
        history.push('/')
    }

    const handleOnEdit = () => {
        history.push(`editPost/${id}`)
    }

    const handleOnLike = () => {
        postLike({ variables: { id: id, user: user } })
        window.location.reload(false);
    }

    if (detailData) {
        console.log(detailData.findPost.post.commentSet)

    }

    return (
        <div>
            {(detailData) ? (
            <Item.Group>
                <Item className="post-detail-item">
                    <Item.Image size='small' src={DEFAULT_IMAGE} />

                    <Item.Content className="post-content">
                        <Item.Header>{detailData.findPost.post.title}</Item.Header>
                        <Item.Meta className="post-user">User: {detailData.findPost.post.user.username}</Item.Meta>
                        <Item.Description className="post-textfield">{detailData.findPost.post.content}</Item.Description>
                            <div className="post-detail-buttons-container">
                                <Button as='div' labelPosition='right'>
                                    <Button onClick={handleOnLike} color='red'>
                                        <Icon name='heart' />
                                        Like
                                    </Button>
                                    <Label as='a' basic color='red' pointing='left'>
                                        {likes}
                                    </Label>
                                </Button>                
                                {(user === detailData.findPost.post.user.username) ? (
                                    <>
                                        <Button onClick={handleOnEdit} className="post-detail-edit-button" color="teal">Edit</Button>
                                        <Button onClick={handleOnDelete} className="post-detail-delete-button" color="red">Delete</Button>
                                    </>
                                ) : null }
                            </div>
                            <div className="comments-container">
                            {(detailData.findPost.post.commentSet) ? (
                                    <Comment.Group>
                                        <Header as='h3' dividing>
                                            Comments
                                        </Header>
                                        {(detailData.findPost.post.commentSet.map(({ id, content, posted, user: { username } }) => {
                                            return (
                                                <CustomComment 
                                                    key={id}
                                                    content={content}
                                                    posted={posted}
                                                    username={username}
                                                />
                                            )
                                        }))}
                                    </Comment.Group>
                            ) : null }
                            <div>
                                <Form reply>
                                    <Form.TextArea />
                                    <Button content='Add Reply' labelPosition='left' icon='edit' primary />
                                </Form>

                            </div>
                        </div>
                    </Item.Content>
                </Item>
            </Item.Group>
        ) : (
            <h1>Loading...</h1>
        ) }
        </div>
    )
}

export default PostDetail
