import React from 'react'
import { Comment, Header } from 'semantic-ui-react'

import CustomComment  from './CustomComment'

//map all comments
const CommentsMap = ({ detailData }) => {
    const { findPost: { post: { commentSet } } } = detailData

    console.log(commentSet)

    return (
        <div>
            {(detailData.findPost.post.commentSet) ? (
                 <Comment.Group>
                        <Header as='h3' dividing>
                            Comments
                        </Header>
                        {(detailData.findPost.post.commentSet.map(({ id, content, posted, user: { username, profile: image }, replySet }) => {
                            return (
                                <CustomComment 
                                    key={id}
                                    id={id}
                                    replys={replySet}
                                    content={content}
                                    posted={posted}
                                    username={username}
                                    image={image}
                                />
                            )
                        })
                    )}
                </Comment.Group>
            ) : null }
        </div>
    )
}

export default CommentsMap
